mapper.select:
SELECT department.dep_name as 'depName' , u.user_name as 'userName', u.standard_score as 'standardScore', title.title_name as 'titleName',
sum(s.score) as score, u.user_id as 'userId' FROM user as u,statistics_detail as s , department,title where u.user_id = s.user_id and department.id = u.dep_id and title.id = u.title_id
AND department.dep_name = #{depName}
AND u.user_name = #{userName}
AND u.user_id = #{userId}
GROUP BY department.dep_name,u.user_name,u.standard_score,title.title_name, u.user_id
ORDER BY s.score DESC
LIMIT #{pageSize} OFFSET #{currentPage}

SELECT u.user_name as 'userName',title.title_name AS 'title',u.standard_score AS 'standardScore', sum(s.score) as score, u.user_id as 'userId' FROM user as u,statistics_detail as s , department ,title where u.user_id = s.user_id and department.id = u.dep_id
AND department.id = #{depId}
GROUP BY department.dep_name,u.user_name ORDER BY s.score DESC
LIMIT #{pageSize} OFFSET #{currentPage}

SELECT * FROM scientific_info where del_flag = 0
AND project_type_name = #{projectTypeName}
AND rule LIKE concat("%",#{rule},"%")
ORDER BY project_type_id DESC LIMIT #{page},#{limit}

SELECT count(project_type_id) FROM scientific_info where del_flag = 0
AND project_type_name = #{projectTypeName}
AND rule LIKE concat("%",#{rule},"%")
